pragma solidity 0.4.24;

import "./A.sol";

contract B {

		A public a;

		constructor(address aAddress) {
			a = A(aAddress);
		}

    function calltest() public view returns(bool) {
        return a.test();
    } //call this function will fail

    function getI() public view returns(uint) {
        return a.i();
    } // call this function will success

    function getMap(address addr) public view returns(address) {
        return a.map(addr);
    } // call this function will fail

		function getAddr() public view returns(address) {
			return a.addr();
		}

}
